mapper.delete:
DELETE FROM TB_HISTORY_IMAGE
WHERE HISTORY_NO IN

    #{id}

DELETE FROM TB_SETUP_HISTORY
WHERE HISTORY_NO IN

    #{id}

mapper.update:
UPDATE TB_SETUP_HISTORY
SET store_name = #{storeName},
    store_address = #{storeAddress},
    model_name = #{modelName},
    history_content = #{historyContent}
WHERE history_no = #{historyNo}

UPDATE TB_HISTORY_IMAGE
SET history_image_name = #{historyImageName}
WHERE history_no = #{historyNo}
  AND history_image_order = #{historyImageOrder}
